import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import profilePic from "../Images/dp.jpg";
import Box from "@material-ui/core/Box";
import Avatar from "@material-ui/core/Avatar";
import Typography from "@material-ui/core/Typography";
import Chip from "@material-ui/core/Chip";
import EmailIcon from "@material-ui/icons/Email";
import ContactPhoneIcon from "@material-ui/icons/ContactPhone";
import SocialMedia from "./SocialMedia";

const useStyles = makeStyles((theme) => ({
  container: {
    //height: "100%",
    [theme.breakpoints.down("lg")]: {
      height: 350,
      paddingTop: theme.spacing(26),
      paddingBottom: theme.spacing(20),
    },
    [theme.breakpoints.down("sm")]: {
      height: 700,
      paddingTop: theme.spacing(10),
      paddingBottom: theme.spacing(10),
    },

    zIndex: 1,
  },

  infoHolder: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    float: "left",
    zIndex: 1,
    [theme.breakpoints.down("lg")]: {
      height: "100%",
      width: "50%",
    },
    [theme.breakpoints.down("sm")]: {
      height: "50%",
      width: "100%",
    },
  },

  imageHolder: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    float: "left",
    zIndex: 1,
    [theme.breakpoints.down("lg")]: {
      float: "right",
      height: "100%",
      width: "50%",
    },
    [theme.breakpoints.down("sm")]: {
      height: "50%",
      width: "100%",
    },
  },

  avatar: {
    [theme.breakpoints.down("lg")]: {
      marginRight: theme.spacing(20),

      width: theme.spacing(40),
      height: theme.spacing(40),
    },
    [theme.breakpoints.down("sm")]: {
      marginRight: theme.spacing(0),
    },
    [theme.breakpoints.down("xs")]: {
      width: theme.spacing(35),
      height: theme.spacing(35),
    },
    zIndex: 1,
  },

  textHolder: {
    [theme.breakpoints.down("lg")]: {
      marginLeft: theme.spacing(20),
    },
    [theme.breakpoints.down("sm")]: {
      marginLeft: theme.spacing(0),
    },
    zIndex: 1,
  },

  nameHolder: {
    maxWidth: "500",
    width: "100%",
    zIndex: 1,
  },

  name: {
    fontWeight: 400,
  },

  infoDescription: {
    marginTop: theme.spacing(2),
    display: "flex",
    alignItems: "center",
  },

  emailHolder: {
    marginLeft: theme.spacing(2),
  },
}));

export default function Home() {
  const classes = useStyles();
  return (
    <div className={classes.container} id="Home">
      <Box className={classes.imageHolder}>
        <Avatar alt="Lim Zi Yang" src={profilePic} className={classes.avatar} />
      </Box>
      <Box className={classes.infoHolder}>
        <Box className={classes.textHolder}>
          <Chip label="Hi there, I'm" color="primary" />
          <Box className="nameHolder">
            <Typography variant="h2" className={classes.name}>
              Lim Zi Yang
            </Typography>
            <Typography variant="h5">Computer Science Student @ NUS</Typography>
            <Typography variant="h6">Contact me @</Typography>
          </Box>
          <Box className={classes.infoDescription}>
            <EmailIcon />
            <div className={classes.emailHolder}>
              <Typography variant="subtitle2">limziyang8@gmail.com</Typography>
            </div>
          </Box>
          <Box className={classes.infoDescription}>
            {/* <ContactPhoneIcon />
            <div className={classes.emailHolder}>
              <Typography variant="subtitle2">+65 8168 9386</Typography>
            </div> */}
          </Box>
          <SocialMedia />
        </Box>
      </Box>
    </div>
  );
}
