import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Tooltip from "@material-ui/core/Tooltip";

import Modal from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";
import Chip from "@material-ui/core/Chip";
import Link from "@material-ui/core/Link";

const useStyles = makeStyles((theme) => ({
  modal: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: "2px solid #000",
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },

  imageHolder: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    marginTop: theme.spacing(0),
    marginBottom: theme.spacing(5),
  },

  image: {
    width: 750,
    height: 180,
    [theme.breakpoints.down("sm")]: {
      width: 550,
      height: 140,
    },
    [theme.breakpoints.down("xs")]: {
      width: 400,
      height: 100,
    },
  },
  rolesHolder: {
    marginTop: theme.spacing(5),
    marginBottom: theme.spacing(5),
  },
  techstack: {
    marginBottom: theme.spacing(2),
  },
  icons: {
    height: 40,
    width: 40,
    marginRight: theme.spacing(2),
  },
}));

export default function ScrollDialog({
  open,
  title,
  image,
  description,
  roles,
  links,
  techStack,
  handleClose,
}) {
  const descriptionElementRef = React.useRef(null);
  React.useEffect(() => {
    if (open) {
      const { current: descriptionElement } = descriptionElementRef;
      if (descriptionElement !== null) {
        descriptionElement.focus();
      }
    }
  }, [open]);
  const classes = useStyles();
  console.log(links);
  return (
    <div>
      {/* <Button onClick={handleClickOpen("paper")}>scroll=paper</Button> */}
      {/* <Button onClick={handleClickOpen}>scroll=body</Button> */}
      <Dialog
        open={open}
        onClose={handleClose}
        maxWidth="md"
        scroll="body"
        aria-labelledby="scroll-dialog-title"
        aria-describedby="scroll-dialog-description"
      >
        {/*---------- Title ----------*/}
        <DialogTitle id="scroll-dialog-title">
          <Typography variant="h3">{title}</Typography>
        </DialogTitle>
        <DialogContent>
          {/*---------- Showcase Image ----------*/}
          <div className={classes.imageHolder}>
            <img
              src={image !== null ? image.showcase : null}
              className={classes.image}
            />
          </div>

          {/*---------- Description ----------*/}
          <DialogContentText
            id="scroll-dialog-description"
            ref={descriptionElementRef}
            tabIndex={-1}
          >
            {description}
          </DialogContentText>

          {/*---------- My Role ----------*/}
          <div className={classes.rolesHolder}>
            <DialogContentText
              id="scroll-dialog-roles"
              ref={descriptionElementRef}
              tabIndex={-1}
            >
              <Typography variant="h5" component="p" color="default">
                My Role:{" "}
              </Typography>
              {roles.map((role) => (
                <Typography
                  className={classes.techstack}
                  variant="body1"
                  component="p"
                  color="default"
                >
                  - {role}
                </Typography>
              ))}
            </DialogContentText>
          </div>

          {/*---------- Tech Stack ----------*/}
          <DialogContentText
            id="scroll-dialog-techStack"
            ref={descriptionElementRef}
            tabIndex={-1}
          >
            <Typography
              className={classes.techstack}
              variant="h5"
              component="p"
              color="default"
            >
              Tech Stack:
            </Typography>
            {techStack.map((icon) => (
              <Tooltip title={icon.name}>
                <img src={icon.img} className={classes.icons} />
              </Tooltip>
            ))}
          </DialogContentText>

          {/*---------- Links ----------*/}
          {links.map((link) => (
            <Link to={link.link}>
              <Chip size="medium" color="primary" label={link.name} />
            </Link>
          ))}
        </DialogContent>
      </Dialog>
    </div>
  );
}
