import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import { Link } from "react-scroll";

const useStyles = makeStyles((theme) => ({
  root: {
    position: "fixed",
    flexGrow: 1,
    top: 0,
    width: "100%",
    backgroundColor: "#e1f5fe",
    zIndex: 2,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  tab: {
    // minWidth: 200,
    width: 200,

    [theme.breakpoints.down("sm")]: {
      // minWidth: 150,
      width: 150,
    },
    [theme.breakpoints.down("xs")]: {
      // minWidth: 100,
      width: 100,
    },
  },
}));

function Tabbar() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Tabs
      className={classes.root}
      value={value}
      onChange={handleChange}
      indicatorColor="inherit"
      textColor="primary"
      centered
    >
      <Link className="toHome" to="Home" smooth={true} duration={1000}>
        <Tab className={classes.tab} label={<div>Home</div>} />
      </Link>
      <Link className="toAbout" to="About" smooth={true} duration={1000}>
        <Tab className={classes.tab} label={<div>About</div>} />
      </Link>
      <Link
        className="toPortfolio"
        to="Portfolio"
        smooth={true}
        duration={1000}
      >
        <Tab className={classes.tab} label={<div>Portfolio</div>} />
      </Link>
      <Link className="toProjects" to="Projects" smooth={true} duration={1000}>
        <Tab className={classes.tab} label={<div>Projects</div>} />
      </Link>
    </Tabs>
  );
}

export default Tabbar;
