import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Avatar from "../Images/avataaars.png";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Icons from "./Icons";

const useStyles = makeStyles((theme) => ({
  container: {
    height: "100%",
    paddingBottom: theme.spacing(100),
  },

  infoHolder: {
    display: "flex",
    height: 500,
    alignItems: "center",
    // justifyContent: "center",
    float: "left",
    width: "50%",
  },

  imageHolder: {
    display: "flex",
    height: 500,
    alignItems: "center",
    justifyContent: "center",
    float: "left",
    width: "50%",
  },

  image: {
    height: 320,
    width: 300,
    marginLeft: theme.spacing(28),
  },

  infoBox: {
    marginLeft: theme.spacing(15),
  },

  title: {
    width: "50%",
  },

  details: {
    marginTop: theme.spacing(2),
    width: "70%",
  },

  icons: {
    marginTop: theme.spacing(2),
  },
}));

export default function About() {
  const classes = useStyles();
  return (
    <div className={classes.container} id="About">
      <Box className={classes.imageHolder}>
        {/* <Typography variant="h4">About Me</Typography> */}
        <img src={Avatar} className={classes.image} img alt="About Avatar" />
      </Box>
      <Box className={classes.infoHolder}>
        <div className={classes.infoBox}>
          <Typography variant="h4" className={classes.title}>
            About Me
          </Typography>
          <Typography variant="subtitle1" className={classes.details}>
            Hello, I'm a Year 2 Computer Science Student who is studying in NUS.
            am passionate about tech development, and I have experience in the
            following:{" "}
          </Typography>
          <Icons />
        </div>
      </Box>
    </div>
  );
}
