        <Card className={classes.card}>
          <CardContent>
            {/* <Typography
              className={classes.title}
              color="textSecondary"
              gutterBottom
            >
              Word of the Day
            </Typography> */}
            <Typography variant="h5" component="h2">
              {a.title}
            </Typography>
            {/* <Typography className={classes.pos} color="textSecondary">

            </Typography> */}
            <Typography variant="body1" component="span" color="default">
              Level:
              {/* <br />
              {'"a benevolent smile"'} */}
            </Typography>
            <Typography
              style={{ fontWeight: 500 }}
              variant="h6"
              component="span"
              color="primary"
            >
              {" "}
              {a.level}
              {/* <br />
              {'"a benevolent smile"'} */}
            </Typography>
          </CardContent>
          <CardActions>
            <Button size="small">Learn More</Button>
          </CardActions>
        </Card>
      ))}
    </Box>
  );
}
