import React from "react";
import { makeStyles } from "@material-ui/core/styles";
// import Avatar from "../Images/avataaars.png";
import Box from "@material-ui/core/Box";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(5),
    marginLeft: theme.spacing(10),
    [theme.breakpoints.down("sm")]: {
      marginLeft: theme.spacing(0),

      display: "flex",
      alignItems: "center",
      justifyContent: "center",
    },
  },
  card: {
    width: 450,
    backgroundColor: "#e8eaf6",
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    fontSize: 14,
  },
  levelHolder: {
    // marginTop: theme.spacing(2),
    marginBottom: theme.spacing(3),
  },

  level: {
    fontWeight: 800,
    fontStyle: "italic",
  },
  description: {
    // marginTop: theme.spacing(2),
    fontStyle: "italic",
  },
  pos: {
    marginBottom: 12,
  },
}));

/*---- My Achievement (Update here, format: {title: , description: , level }) ----*/
const achievements = [
  {
    title: "Orbital Project",
    level: "Apollo 11",
    description:
      "A Summer Independent Project designed by NUS School Of Computing, where Year 1 Computing students work together in pairs to develop software applications in an independent learning environment.",
  },
];

export default function Achievements() {
  const classes = useStyles();
  const bull = <span className={classes.bullet}>â€¢</span>;

  return (
    <Box className={classes.container}>
      {achievements.map((a) => (
        <Card className={classes.card}>
          <CardContent>
            {/* <Typography
              className={classes.title}
              color="textSecondary"
              gutterBottom
            >
              Word of the Day
            </Typography> */}
            <Typography variant="h5" component="h2">
              {a.title}
            </Typography>
            {/* <Typography className={classes.pos} color="textSecondary">

            </Typography> */}
            <div className={classes.levelHolder}>
              <Typography variant="body1" component="span" color="default">
                Achievement Level:{" "}
              </Typography>
              <Typography
                className={classes.level}
                variant="body1"
                component="span"
                color="primary"
              >
                {" "}
                {a.level}
              </Typography>
            </div>
            <Typography
              className={classes.description}
              variant="body1"
              component="p"
              color="default"
            >
              {a.description}
              {/* <br />
              {'"a benevolent smile"'} */}
            </Typography>
          </CardContent>
        </Card>
      ))}
    </Box>
  );
}
