import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Grid from "@material-ui/core/Grid";
import Container from "@material-ui/core/Container";
import ModPlanner from "./My Projects/Modplanner";
import Dialog from "./Dialog";

const useStyles = makeStyles((theme) => ({
  container: {
    height: 750,
    paddingTop: theme.spacing(15),
    paddingBottom: theme.spacing(15),
    zIndex: 1,
    [theme.breakpoints.down("sm")]: {
      //   paddingTop: theme.spacing(5),
      paddingBottom: theme.spacing(50),
    },
  },

  titleHolder: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
  },
  card: {
    height: "100%",
    display: "flex",
    flexDirection: "column",
    backgroundColor: theme.palette.background.paper,
  },
  cardMedia: {
    //paddingTop: "56.25%", // 16:9
    marginTop: "10%",
    marginBottom: "10%",
    // backgroundColor: "#f3f3f3",
  },
  image: {
    height: "100%",
    width: "100%",
  },
  logo: {
    marginLeft: theme.spacing(1),
    height: 20,
    width: 20,
  },

  cardTitleHolder: {
    display: "flex",
    alignItems: "baseline",
  },
  cardContent: {
    flexGrow: 1,
  },
  cardButton: {
    // fontWeight: 800,
  },
}));

// const cards = [1, 2, 3, 4, 5, 6, 7, 8, 9];
/*---- My Projects (Update here, format: {title: , shortDescription, description: , links: , techStack:   }) ----*/
const projects = [ModPlanner];

export default function Portfolio() {
  const classes = useStyles();

  const [open, setOpen] = React.useState(false);
  const [title, setTitle] = React.useState("");
  const [image, setImage] = React.useState(null);
  const [description, setDescription] = React.useState("");
  const [roles, setRoles] = React.useState([]);
  const [links, setLinks] = React.useState([]);
  const [techStack, setTechStack] = React.useState([]);

  const handleClick = (title, image, description, roles, links, techStack) => {
    setOpen(true);
    setTitle(title);
    setImage(image);
    setDescription(description);
    setRoles(roles);

    setLinks(links);
    setTechStack(techStack);
  };

  const handleClose = () => {
    setOpen(false);
  };
  return (
    <div className={classes.container} id="Projects">
      <Box className={classes.titleHolder}>
        <Typography variant="h3">My Projects</Typography>
      </Box>
      <Container className={classes.cardGrid} maxWidth="md">
        {/* End hero unit */}
        <Grid container spacing={4}>
          {projects.map((project) => (
            <Grid item key={project} xs={12} sm={6} md={4}>
              <Card className={classes.card}>
                <CardMedia className={classes.cardMedia} title="Image title">
                  <img
                    className={classes.image}
                    src={project.image.showcase}
                    alt="showcase"
                  />
                </CardMedia>
                <CardContent className={classes.cardContent}>
                  <div className={classes.cardTitleHolder}>
                    <Typography gutterBottom variant="h5" component="span">
                      {project.title}
                      <img
                        className={classes.logo}
                        src={project.image.logo}
                        alt="logo"
                      />
                    </Typography>
                  </div>
                  <Typography component="p">
                    {project.shortDescription}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button
                    size="small"
                    color="primary"
                    onClick={() =>
                      handleClick(
                        project.title,
                        project.image,
                        project.description,
                        project.roles,
                        project.links,
                        project.techStack
                      )
                    }
                    className={classes.cardButton}
                  >
                    More Info
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
      <Dialog
        open={open}
        title={title}
        image={image}
        description={description}
        roles={roles}
        links={links}
        techStack={techStack}
        handleClose={handleClose}
      />
    </div>
  );
}
