import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Avatar from "../Images/avataaars.png";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Icons from "./Icons";

const useStyles = makeStyles((theme) => ({
  container: {
    zIndex: 1,
    [theme.breakpoints.down("lg")]: {
      height: 350,
      paddingTop: theme.spacing(5),
      paddingBottom: theme.spacing(5),
    },
    [theme.breakpoints.down("sm")]: {
      height: 700,
      paddingTop: theme.spacing(0),
      paddingBottom: theme.spacing(5),
    },
  },

  infoHolder: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    float: "right",
    [theme.breakpoints.down("lg")]: {
      height: "100%",

      width: "50%",
    },
    [theme.breakpoints.down("sm")]: {
      paddingTop: theme.spacing(10),
      height: "50%",
      width: "80%",
    },
  },

  imageHolder: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    float: "left",
    [theme.breakpoints.down("lg")]: {
      height: "100%",

      width: "50%",
    },
    [theme.breakpoints.down("sm")]: {
      height: "50%",

      width: "100%",
    },
  },

  image: {
    height: 350,
    width: 330,
    [theme.breakpoints.down("lg")]: {
      marginLeft: theme.spacing(20),
    },
    [theme.breakpoints.down("sm")]: {
      marginLeft: theme.spacing(0),
    },
  },

  infoBox: {
    [theme.breakpoints.down("lg")]: {
      marginLeft: theme.spacing(15),
    },
    [theme.breakpoints.down("sm")]: {
      marginLeft: theme.spacing(0),
    },
    // [theme.breakpoints.down("xs")]: {
    //   marginLeft: theme.spacing(5),
    // },
  },

  title: {
    width: "50%",
    [theme.breakpoints.down("sm")]: {},
  },

  details: {
    marginTop: theme.spacing(2),
    width: "80%",
    // [theme.breakpoints.down("sm")]: {
    //   width: "90%",
    // },
    [theme.breakpoints.down("xs")]: {
      width: "90%",
    },
  },

  icons: {
    marginTop: theme.spacing(2),
  },
}));

export default function About() {
  const classes = useStyles();
  return (
    <div className={classes.container} id="About">
      <Box className={classes.imageHolder}>
        {/* <Typography variant="h4">About Me</Typography> */}
        <img src={Avatar} className={classes.image} img alt="About Avatar" />
      </Box>
      <Box className={classes.infoHolder}>
        <div className={classes.infoBox}>
          <Typography variant="h4" className={classes.title}>
            About Me
          </Typography>
          <Typography variant="subtitle1" className={classes.details}>
            Hello, I'm a Year 2 Computer Science Student who is studying in NUS.
            I am looking into specializing in Artificial Intelligence (A.I.) and
            Software Engineering. I have experience in the following:{" "}
          </Typography>
          <Icons />
        </div>
      </Box>
    </div>
  );
}
