import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";

import Button from "@material-ui/core/Button";
import CameraIcon from "@material-ui/icons/PhotoCamera";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import CssBaseline from "@material-ui/core/CssBaseline";
import Grid from "@material-ui/core/Grid";
import Toolbar from "@material-ui/core/Toolbar";
import Container from "@material-ui/core/Container";
import ModPlanner from "../Images/modplanner.png";
import Link from "@material-ui/core/Link";

const useStyles = makeStyles((theme) => ({
  container: {
    height: 750,
    paddingTop: theme.spacing(15),
    paddingBottom: theme.spacing(50),
    zIndex: 1,
    [theme.breakpoints.down("sm")]: {
      //   paddingTop: theme.spacing(5),
      paddingBottom: theme.spacing(50),
    },
  },

  titleHolder: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },

  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
  },
  card: {
    height: "100%",
    display: "flex",
    flexDirection: "column",
  },
  cardMedia: {
    paddingTop: "56.25%", // 16:9
  },
  cardContent: {
    flexGrow: 1,
  },
}));

const cards = [1, 2, 3, 4, 5, 6, 7, 8, 9];

/*---- My Projects (Update here, format: {title: , shortDescription, description: , links: , techStack:   }) ----*/
const projects = [
  {
    title: "The Mod Planner",
    shortDescription:
      "A web application that allows NUS students to plan modules, as well as share planners in the form of reviews.",
    image: { ModPlanner },
    description: "",
    links: [],
    techStack: [],
  },
];

export default function Portfolio() {
  const classes = useStyles();

  return (
    <div className={classes.container} id="Projects">
      <Box className={classes.titleHolder}>
        <Typography variant="h3">My Projects</Typography>
      </Box>
      <Container className={classes.cardGrid} maxWidth="md">
        {/* End hero unit */}
        <Grid container spacing={4}>
          {projects.map((card) => (
            <Grid item key={card} xs={12} sm={6} md={4}>
              <Card className={classes.card}>
                <CardMedia
                  className={classes.cardMedia}
                  image={card.image}
                  title="Image title"
                />
                <CardContent className={classes.cardContent}>
                  <Typography gutterBottom variant="h5" component="h2">
                    {card.title}
                  </Typography>
                  <Typography>{card.shortDescription}</Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" color="primary">
                    More Info
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    </div>
  );
}
